import React from 'react';
import ScrollTrigger from 'react-scroll-trigger';

class RecentWorkThumbnails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inFocus: false
    }
  }

  render() {
    const { handleModal, content = {} } = this.props
    const { label = '', fields = [] } = content
    const { inFocus } = this.state
    const thumbnails = fields.map((element, index) => {
      const { image, label: image_label, link = '' } = element
      const thumbnail_image = inFocus ? image : ''
      const shadow = inFocus ? 'shadow-2' : ''
      const fieldsContent = link ? <div key={index} className="w-third pa3">
        <a
          className={`link mw5 dt hide-child br2 cover bg-center h-100 ${shadow} fade-in`}
          target="_blank"
          rel='noreferrer noopener'
          href={link}
          style={{ backgroundImage: `url(${thumbnail_image})` }}
        >
          <span className="white dtc v-mid w-100 h-100 child bg-black-40 pa5">
            {image_label}
          </span>
        </a>
      </div> : <div key={index} className="w-third pa3">
          <div
            className={`link mw5 dt hide-child br2 cover bg-center h-100 ${shadow} fade-in`}
            style={{ backgroundImage: `url(${thumbnail_image})` }}
            onClick={() => { handleModal(thumbnail_image) }}
          >
            <span className="white dtc v-mid w-100 h-100 child bg-black-40 pa5">
              {image_label}
            </span>
          </div>
        </div>

      return fieldsContent
    })

    return (
      <div className="recent-design vh-75 flex flex-column justify-center">
        <div className="w-100">
          <h1>{label}</h1>
        </div>
        <ScrollTrigger onEnter={() => { this.setState({ inFocus: true }) }}>
          <div className="examples flex flex-wrap h-50">
            {thumbnails}
          </div>
        </ScrollTrigger>
      </div>
    )
  }
}

export default RecentWorkThumbnails;
