import React from 'react';
import ScrollTrigger from 'react-scroll-trigger';

class RecentWorkThumbnails extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inFocus: false
    }
  }

  render() {
    const { handleModal, content = {} } = this.props
    const { label = '', fields = [] } = content
    const { inFocus } = this.state
    const thumbnails = fields.map((element, index) => {
      const { image, label: image_label, link = '' } = element
      const fieldsContent = link ? <div key={index} className="w-third pa3 h5">
        <a
          className={`link dt hide-child br2 cover bg-center h-100 shadow-2 fade-in center w-100`}
          target="_blank"
          rel='noreferrer noopener'
          href={link}
          style={{ backgroundImage: `url(${image})` }}
        >
          <span className="washed-green dtc v-mid child bg-black-40">
            {image_label}
          </span>
        </a>
      </div> : <div key={index} className="w-third pa3 h4" onClick={() => { handleModal(image) }}>
          <div
            className={`link mw4 dt hide-child br2 cover bg-center h-100 shadow-2 fade-in center w-100`}
            style={{ background: `url(${image})` }}
          >
            <span className="washed-green dtc v-mid child bg-black-40">
              {image_label}
            </span>
          </div>
        </div>

      return fieldsContent
    })

    return (
      <div className="recent-design  vh-75 flex flex-column justify-center">
        <div className="w-100 pacifico">
          <h1>{label}</h1>
        </div>
        <ScrollTrigger className="mt2" onEnter={() => { this.setState({ inFocus: true }) }}>
          <div className="examples flex flex-wrap">
            {inFocus ? thumbnails : null}
          </div>
        </ScrollTrigger>
      </div>
    )
  }
}

export default RecentWorkThumbnails;
