import React, { Component } from 'react';
import styled from 'styled-components'
import Percentage from './components/Percentage';
import Modal from './components/Modal';
import SocialMedia from './components/social_media'
import DanielDouangkesone from './components/daniel_douangkesone';
import RecentWorkThumbnails from './components/recent_work_thumbnails';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalOpen: false,
      modalImage: '',
      introVisible: true
    }
    this.handleModal = this.handleModal.bind(this)
    this.escFunction = this.escFunction.bind(this)
  }

  componentDidMount(){
    // Added: Gives users ability to ESC out of modal
    document.addEventListener("keydown", this.escFunction, false);
    // Added: Used to fade out intro text
    window.addEventListener('scroll', () => {
      const { introVisible } = this.state
      const { pageYOffset } = window
      console.log({pageYOffset});

      // if(pageYOffset === 0) {
      //   opacityValue = 1
      // }
      // else if(pageYOffset >= 700) {
      //   opacityValue = 0
      // }
      // else if(pageYOffset > 185 && introOpacity >= 0 ) {
      //   opacityValue =  opacityValue - .1
      // }
      // else if(pageYOffset < 185 && introOpacity <= 1.3) {
      //   opacityValue =  opacityValue + .2
      // }
      // this.setState({introOpacity: opacityValue})

      if(pageYOffset > 350 && !introVisible) {
        console.log('danfoo');
        this.setState({introVisible: false})
      }
      else if(pageYOffset < 350 && !introVisible) {
        console.log('danfoo2');
        this.setState({introVisible: true})
      }


    })
  }

  handleModal(modalImage) {
    const { modalOpen } = this.state
    this.setState({modalOpen: modalOpen ? false : true, modalImage})
  }

  escFunction(event){
    if(event.keyCode === 27) {
      this.setState({modalOpen: false})
    }
  }

  render() {
    const { modalOpen, modalImage, introVisible } = this.state
    const { 
      name = '',
      sections = [],
      skills = [],
      social_media = []
      } = DanielDouangkesone
     const recentWork = sections.map((element, index) => {
      return <RecentWorkThumbnails key={index} content={element} handleModal={this.handleModal} index={index} />
    })
    const skillslist = skills.map(element => { 
      const {label = '', percent} = element
      return (
        <Percentage percent={percent} label={label} />
      )
    })

    return (
      <div className="App">
        { modalOpen ? <Modal image={modalImage} handleModal={this.handleModal} /> : null }
        <div className="background-landscape"></div>
          <div className="fixed top-0 vh-100 flex items-center ph4 white">
            <span>
              <h2 className="fade-out">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut 
                labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi 
                ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum 
                dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia 
                deserunt mollit anim id est laborum.
              </h2>
            </span>
          </div>
        <div className="staatliches ph5 z-1 fixed top-0 w-100 tc name shadow-3 black bg-washed-green">
          <h2 className="mb0 mt0 f-subheadline">
            {name}
          </h2>
          <SocialMedia content={social_media} />  
        </div>
        <div className="w-100 w-50-l vh-100 spacer"></div>
        <div className="w-100 w-50-l fr tc ph4 white-o2">
          <div className="hello vh-75 flex flex-column justify-center">
            <h1>Hello</h1>
            <span>
              I'm a web designer / developer based out of Austin, Tx. I started taking freelance design jobs and
              naturally progressed into web development. I love aspects of both fields, and enjoy building sites from
              start to finish. I am available for work - if you would like to build something -
            </span>
            <a href="www.google.com">Get in Touch</a>
          </div>
          {recentWork}
          <div className="skills flex flex-column vh-75 flex flex-column justify-center">
            <div className="w-100">
              <h1>Related Skills</h1>
            </div>
            {skillslist}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
