import React from 'react';
import ScrollTrigger from 'react-scroll-trigger';

class RecentWorkThumbnails extends React.Component {

  render() {
    const { label = '', fields = [] } = this.props.content
    console.log(this.props);

    return (
      <div className="recent-design  vh-75 flex flex-column justify-center">
        <div className="w-100">
          <h1>{label}</h1>
        </div>
        <ScrollTrigger onEnter={() => { console.log('footang') }}>
          <div className="examples flex flex-wrap h-50">
            {fields.map((element, index) => {
              const { image, label: image_label } = element
              return (
                <div key={index} className="w-third pa3">
                  <a
                    className={`link mw5 dt hide-child br2 cover bg-center h-100 shadow-2 fade-in`}
                    style={{ backgroundImage: `url(${image})` }}
                    onClick={() => { this.handleModal(image) }}
                  >
                    <span className="white dtc v-mid w-100 h-100 child bg-black-40 pa5">
                      {image_label}
                    </span>
                  </a>
                </div>
              )
            })}
          </div>
        </ScrollTrigger>
      </div>
    )
  }
}

export default RecentWorkThumbnails;
