import React, { Component } from 'react';
import back from './assets/back.png'
import { Line } from 'rc-progress';
import Modal from './components/Modal'
import imageList from './components/image_list'
import websiteList from './components/website_list'



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalOpen: false,
      modalImage: ''
    }
    this.handleModal = this.handleModal.bind(this)
    this.escFunction = this.escFunction.bind(this)
  }

  componentDidMount(){
    document.addEventListener("keydown", this.escFunction, false);
  }

  handleModal(modalImage) {
    const { modalOpen } = this.state
    this.setState({modalOpen: modalOpen ? false : true, modalImage})
  }

  escFunction(event){
    if(event.keyCode === 27) {
      this.setState({modalOpen: false})
    }
  }

  render() {
    const { modalOpen, modalImage } = this.state

    return (
      <div className="App">
        { modalOpen ? <Modal image={modalImage} handleModal={this.handleModal} /> : null }
        <div className="staatliches f-subheadline h4 mb5">
          <h2 className="mb0 mt0 ph5 z-1 fixed top-0 w-100 tc  name">
            Daniel Douangkesone
          </h2>
        </div>
        <div className="w-100 w-50-l pl3 fr vh-100 tc pr5">
          <div className="hello vh-75 flex flex-column justify-center">
            <h1>Hello</h1>
            <span>
              I'm a web designer / developer based out of Austin, Tx. I started taking freelance design jobs and
              naturally progressed into web development. I love aspects of both fields, and enjoy building sites from
              start to finish. I am available for work - if you would like to build something -
            </span>
            <a href="www.google.com">Get in Touch</a>
          </div>
          <div className="recent-web vh-75 flex flex-column justify-center">
            <div className="w-100">
                <h1>Recent web work</h1>
            </div>
            <div className="examples flex h-50">
              {websiteList.map((element, key) => {
                const {image, label, link} = element
                return (
                  <div key={key} className="w-50 pa3">
                    <a href={link} target="_blank" rel='noreferrer noopener' className="link mw5 dt hide-child br2 cover bg-center h-100" style={{backgroundImage: `url(${image})`}}>
                      <span className="white dtc v-mid w-100 h-100 child bg-black-40 pa5">
                        {label}
                      </span>
                    </a>
                  </div>
                )
              })}
            </div>
          </div>
          <div className="recent-design  vh-75 flex flex-column justify-center">
            <div className="w-100">
                <h1>Recent Design Work</h1>
            </div>
            <div className="examples flex flex-wrap h-50">
            {imageList.map((element, key) => {
              const {image, label} = element
              return (
                <div key={key} className="w-third pa3">
                <a className="link mw5 dt hide-child br2 cover bg-center h-100" style={{backgroundImage: `url(${image})`}} onClick={() => {this.handleModal(image)}}>
                  <span className="white dtc v-mid w-100 h-100 child bg-black-40 pa5">
                    {label}
                  </span>
                </a>
              </div>
              )
            })}
            </div>
          </div>
          <div className="skills flex flex-column vh-75 flex flex-column justify-center">
            <div className="w-100">
              <h1>Related Skills</h1>
            </div>
            <div className="w-100 flex items-center">
              <span>
                Javascript
              </span>
              <div>
              <Line percent="10" strokeWidth="7" trailWidth="0" strokeColor="#D3D3D3" />
              </div>
            </div>
          </div>
        </div>
        <div className="w-50">
          <div className="fixed bottom-0 w-25">
            <img alt="profile" src={back} />
          </div>
        </div>

      </div>
    );
  }
}

export default App;
