import React, { Component } from 'react';
import ScrollTrigger from 'react-scroll-trigger';
import Percentage from './components/Percentage';
import Modal from './components/Modal';
import DanielDouangkesone from './components/daniel_douangkesone';
import RecentWorkThumbnails from './components/recent_work_thumbnails';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalOpen: false,
      modalImage: '',
      skills: false,
    }
    this.handleModal = this.handleModal.bind(this)
    this.escFunction = this.escFunction.bind(this)
  }

  componentDidMount(){
    document.addEventListener("keydown", this.escFunction, false);
  }

  handleModal(modalImage) {
    const { modalOpen } = this.state
    this.setState({modalOpen: modalOpen ? false : true, modalImage})
  }

  escFunction(event){
    if(event.keyCode === 27) {
      this.setState({modalOpen: false})
    }
  }

  render() {
    const { modalOpen, modalImage, design, skills, sites } = this.state
    const { 
      name = '',
      profile_image = '',
      sections = [],
      skills: skillsList = []
      } = DanielDouangkesone
     const recentWork = sections.map((element, index) => {
      return <RecentWorkThumbnails key={index} content={element} handleModal={this.handleModal} index={index} />
    })

    return (
      <div className="App">
        { modalOpen ? <Modal image={modalImage} handleModal={this.handleModal} /> : null }
        <div className="staatliches f-subheadline h4 mb5">
          <h2 className="mb0 mt0 ph5 z-1 fixed top-0 w-100 tc  name">
            {name}
          </h2>
        </div>
        <div className="w-100 w-50-l pl3 fr vh-100 tc pr5">
          <div className="hello vh-75 flex flex-column justify-center">
            <h1>Hello</h1>
            <span>
              I'm a web designer / developer based out of Austin, Tx. I started taking freelance design jobs and
              naturally progressed into web development. I love aspects of both fields, and enjoy building sites from
              start to finish. I am available for work - if you would like to build something -
            </span>
            <a href="www.google.com">Get in Touch</a>
          </div>
          {recentWork}
          <div className="skills flex flex-column vh-75 flex flex-column justify-center">
            <div className="w-100">
              <h1>Related Skills</h1>
            </div>
            <ScrollTrigger onEnter={() => {this.setState({skills: true})}}>
            {skills ? skillsList.map(element => { 
              const {label = '', percent} = element
              return (
                <Percentage percent={percent} label={label} />
              )
            }) : null}
            </ScrollTrigger>
          </div>
        </div>
        <div className="w-50">
          <div className="fixed bottom-0 w-25">
            <img alt="profile" src={profile_image} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
