import React from 'react';
import { Line } from 'rc-progress';

class Percentage extends React.Component {
  constructor() {
    super();
    this.state = {
      count: 0,
    };
    this.increase = this.increase.bind(this);
    this.restart = this.restart.bind(this);
  }

  componentDidMount() {
    const { percent } = this.props
    this.setState({ count: percent })
    this.increase();
  }

  increase() {
    const count = this.state.count + 1;
    if (count >= 100) {
      clearTimeout(this.tm);
      return;
    }
    this.setState({ count });
    this.tm = setTimeout(this.increase, 10);
  }

  restart() {
    clearTimeout(this.tm);
    this.setState({ count: 0 }, () => {
      this.increase();
    });
  }

  render() {
    const { percent = 60, label = 'HTML' } = this.props
    const { count } = this.state

    return (
      <div className="w-100 flex items-center justify-between ph5">
        <span>
          {label}
        </span>
        <div className="w-75">
          <Line percent={count} strokeWidth="7" trailWidth="0" strokeColor="#D3D3D3" />
        </div>
        <span>
          {count}
        </span>
      </div>
    )
  }
}

export default Percentage
